	addbook = `legado://import/addToBookshelf?src=${encodeURIComponent(booksrc)},{origin:'https://webapi.gongzicp.com'}`;

 fetch(booksrc)
  .then((response) => response.json())
  .then((data) =>{
  	title = data.data.novel_name

  	//获取作者名称
author = data.data.author_nickname;

//create(搜索的内容,"bookName or bookAuthor",距离顶部距离,[跳转其他url])
create(title,"addBookshelf",14,addbook);
create(title,"bookName",18)
create(author,"bookAuthor",22)


  	});
function create(name,type,top,url){
 	    type = type+"="+name;
 	    url = url!=undefined?url+"#"+type:"";

     let 	typebutton;
     if(/bookName/.test(type)){
     	    typebutton="搜索书名"
     	}else if(/bookAuthor/.test(type)){
     		   typebutton="搜索作者"
     	}else{
     			   typebutton="加入书架"
     	}

    	let Url = location.search!=""?location.search.replace(/&*book[AN].*?=.*/,'')+"&"+type:window.location.pathname+"?"+type;
    	url = url!=""?url:Url;
