      // 获取当前的 offset 值
      let currentOffset = parseInt(currentUrl.searchParams.get('offset'));

      // 计算新的 offset 值（减少 20）
      let newOffset = Math.max(0, currentOffset - 20);

      // 更新 URL 中的 offset 参数
      currentUrl.searchParams.set('offset', newOffset);

      // 跳转至新的 URL
      window.location.href = currentUrl.toString();
      updateCurrentPage(getCurrentPage(newOffset));
