        tags = categories[i].select('.gap-2 a');
        for (j in tags) {
            exploreUrl.push(explore('标签', tags[j].text(), tags[j].attr('href')));
        }
    }

    `搜索::${Get('url')}/@js:\`{{Get('url')}}/search?q={{Skey('搜索：')}}&t=${Type}&page={{page}}\`\n
${exploreUrl.join('\n')}`
}

