injectJs:
document.getElementById("app").remove();

// 获取书籍链接
		bookid = String(window.location.href).replace(/^.*\//,'');
		booksrc = "http://app-cdn.jjwxc.net/androidapi/novelbasicinfo?novelId="+bookid;

booksrc = `legado://import/addToBookshelf?src=${encodeURIComponent(booksrc)}`;

		//获取书籍名称
title = document.querySelector(".big.o").innerText.replace(/首页>/,'');

//获取作者名称
author = document.querySelector(".authorname-content a").innerText;

//跳转作者url[可省略]
document.querySelector(".authorname-content a").href;

//create(搜索的内容,"bookName or bookAuthor",距离顶部距离,[跳转其他url])

create(title,"addBookshelf",14,booksrc);
create(title,"bookName",18)
create(author,"bookAuthor",22)


 function create(name,type,top,url){
 	    type = type+"="+name;
 	    url = url!=undefined?url+"#"+type:"";

     let 	typebutton;
     if(/bookName/.test(type)){
     	    typebutton="搜索书名"
     	}else if(/bookAuthor/.test(type)){
     		   typebutton="搜索作者"
     	}else{
     			   typebutton="加入书架"
     	}

    	let Url = location.search!=""?location.search.replace(/&*book[AN].*?=.*/,'')+"&"+type:window.location.pathname+"?"+type;
    	url = url!=""?url:Url;

    	A = document.createElement("a");
		 A.href =url.replace(/#addBookshelf.*/,'')

		A.innerHTML = `<button style="outline:none; position:fixed; top:${top}%; right:7%; z-index:999; border-radius:5px; color:#166188;background:#fff;padding:4px;border:1px solid #000"><b>${typebutton}</b></button>`;

    	document.body.appendChild(A);
 	}

ruleContent:
{{/data:/.test(baseUrl)?java.startBrowser(baseUrl,"感谢你的支持"):""}}
<div class="booklist_desc">
    <p class="listname">
        <span class="level">
            &emsp;{{$.data.booklist_desc.level}}级&emsp;
        </span>
          {{$.data.booklist_desc.listname}}
     </p>
     <span class="dateline">
         {{$.data.booklist_desc.dateline||$.data.booklist_desc.create_time}}
     </span>
     <p class="des">
         荐书描述 共{{$.data.booklist_desc.booknum}}本
     </p>
     <div class="introduction">
           <p>{{$.data.booklist_desc.introduction}}</p>
     </div>
</div>

<br>
<br>

<js>
java.toast("加载中....")
try{
   function getBooklist(list){
   	let html="";
   	for(i in list){
    	  $ = list[i];
    	  let novelid = "https://m.jjwxc.net/book2/"+$.novelid;
    	  let cover = $.cover;
    	  let novelname = $.novelname;
    	  let authorname = $.authorname;
    	  let novelstep_name = $.novelstep_name || $.novelstep_str;
    	  let novelsize = $.novelsize;
    	  let novel_review_score = $.novel_review_score || ($.fav_count_str+"收藏");
    	  let novelstar = $.novelstar || 0;
    	  let s="";
    	  for(i=0;i<novelstar;i++){s+="★"}
    	  novelstar=s;
    	  let novelintroshort = $.novelintroshort;
    	  let comment = $.comment || "";
    	  let novelclass_str = $.novelclass_str;

	      html +=`
	         <div class="book">
	            <div class="bookdes">
	             <div class="pic">
	                 <img src="${cover}" referrerpolicy="no-referrer">
	             </div>
	             <div class="details>
	                  <p class="bookname">
	                      <span class="name">
	                         <a href="${novelid}">${novelname}</a>
	                      </span>
	                      <br>
	                      <span class="author">
	                         ——${authorname}
	                      </span>
	                  </p>	
	                  <p class="tag">
	                  ${novelsize}•${novelstep_name}•${novel_review_score}<br><br>
	                    ${novelintroshort}<br><br>
	                    ${novelclass_str}<br><br>
